package modchu.pflm;import modchu.lib.Modchu_Debug;import modchu.lib.Modchu_ThreadVersionCheck;public class PFLM_ThreadVersionCheck extends Modchu_ThreadVersionCheck {	@Override	protected void checkRelease(String s) {		Modchu_Debug.lDebug("PFLM_ThreadVersionCheck checkRelease Version="+s);		if (PFLM_Main.checkRelease(s)) PFLM_Main.setNewRelease(s);	}}